#!/bin/bash# Check if PORT is provided by Renderif [ -z "$PORT" ]; then  echo "ERROR: PORT environment variable not set!"  exit 1fi# Update the Mosquitto configuration with the Render PORTsed -i "s/^listener.*/listener $PORT/" /mosquitto/config/mosquitto.conf# Start Mosquitto/usr/sbin/mosquitto -c /mosquitto/config/mosquitto.conf